mapper.update:
update base_access_info
set is_enable  = #{isEnable},
    updater= #{updater},
    update_time=#{updateTime}
where acc_point_res_id = #{accPointResId}

mapper.select:
select count(*)
from base_access_info
WHERE process_id = #{processId}

SELECT count(*)
FROM base_access_info
WHERE process_id = #{processIdNew}
  AND acc_point_res_id != #{accPointResId}
