mapper.select:
select count(*)
from eqi_equipment
where equipment_id = #{equipmentId}

select count(*)
from eqi_equipment
where equipment_ip = #{equipmentIp}
  and equipment_int != #{equipmentInt}

select count(*)
from eqi_equipment
where equipment_ip = #{equipmentIp}

mapper.update:
update eqi_equipment
set enable = #{enable}
where equipment_int = #{equipmentInt}

update eqi_equipment
set enable = #{enable}
where equipment_int = #{equipmentInt}

mapper.select:
SELECT eqi.*, bei.equip_type_name, bai.acc_point_res_name, bpi.process_name, bfi.factory_code, bmi.matrix_code, cur.USER_NAME AS createName, eqiDev.app_id FROM eqi_equipment AS eqi LEFT JOIN base_equip_info AS bei ON eqi.equip_type_id = bei.equip_type_id LEFT JOIN base_access_info AS bai ON eqi.acc_point_res_id = bai.acc_point_res_id LEFT JOIN base_process_info AS bpi ON bai.process_id = bpi.process_id LEFT JOIN base_factory_info AS bfi ON bpi.factory_id = bfi.factory_id LEFT JOIN base_matrix_info AS bmi ON bfi.matrix_id = bmi.matrix_id LEFT JOIN cap_user AS cur ON eqi.creator = cur.USER_ID LEFT JOIN eqi_device AS eqiDev ON eqi.equipment_int = eqiDev.equipment_int
and app_id is null
and eqi.equipment_id like '%${equipmentId}%'
and bpi.process_name like '%${processName}%'
order by ${field} ${order}
limit ${begin},${limit}
