mapper.select:
SELECT dev.device_id, equ.equipment_int, equ.equipment_id, equ.equipment_name, mdr.plc_status, mdr.plc_monitor_time FROM mon_device_real AS mdr LEFT JOIN eqi_device AS dev ON mdr.app_id = dev.app_id LEFT JOIN eqi_equipment AS equ ON dev.equipment_int = equ.equipment_int
and equ.equipment_id like '%${equipmentId}%'
and mdr.plc_status like '%${plcStatus}%'
order by ${field} ${order}
limit ${begin},${limit}

SELECT dev.device_id, equ.equipment_int, equ.equipment_id, equ.equipment_name, mdr.rfid_status, mdr.antenna_status, mdr.rfid_monitor_time FROM mon_device_real AS mdr LEFT JOIN eqi_device AS dev ON mdr.app_id = dev.app_id LEFT JOIN eqi_equipment AS equ ON dev.equipment_int = equ.equipment_int
and equ.equipment_id like '%${equipmentId}%'
and mdr.rfid_status like '%${rfidStatus}%'
and mdr.antenna_status like '%${antennaStatus}%'
order by ${field} ${order}
limit ${begin},${limit}
