mapper.select:
select * from base_process_info


        and info_id like '%${infoId}%'



    order by ${field} ${order}


    limit ${begin},${limit}

SELECT a.info_id, a.process_code, a.process_name
FROM base_process_info a LEFT JOIN base_tree b ON a.info_id = b.info_id
WHERE logic_states = "0" AND parent_id=#{infoId}
